      allowNull: {
        args: true,
      },
    },
    mobileNumber: {
      type: DataTypes.STRING,
      allowNull: {
        args: true,
      },
    },
    email: {
      allowNull: {
        args: false,
        msg: 'Please provide a valid email',
      },
      type: DataTypes.STRING,
      unique: {
        args: true,
        msg: 'Email already exists',
      },
    },
    password: {
      type: DataTypes.STRING,
      allowNull: {
        args: false,
        msg: 'Please provide a password',
      },
    },
    sex: {
      type: DataTypes.STRING,
    },
    isAdmin: {
      type: DataTypes.BOOLEAN,
      defaultValue: false,
    },
    dateOfBirth: {
      type: DataTypes.DATE,
    },
  }, {
    timestamps: false,
    scopes: {
      withoutPassword: {
        attributes: { exclude: ['password'] },
      },
    },
  });
  /**
   * compares if the passed arguments are equal
   * @param {string} password
   * @param {object} user
   * @returns {boolean} true or false
   */
  User.prototype.comparePassword = (password, user) => bcrypt.compareSync(password, user.password);
  /**
   * encrypt a user's password
   * @param {string} password
   * @returns {string} hashed password
   *
   */
  User.prototype.encryptPassword = (password) => bcrypt.hashSync(password, bcrypt.genSaltSync(6));
  User.beforeCreate((user) => {
    user.password = user.encryptPassword(user.password);
  });
  User.associate = (models) => {
    User.hasMany(models.Message, {
      onDelete: 'CASCADE',
      foreignKey: 'userId',
      target: 'id',
    });
    User.belongsToMany(models.Group, {
      onDelete: 'CASCADE',
      through: 'GroupMembers',
    });
  };
  return User;
};

