import jwt from 'jsonwebtoken';
import dotenv from 'dotenv';
import Response from '../../utils/response';

dotenv.config();
const response = new Response();

class Authenticate {
  /**
   * @param  {} id
   * @param  {} email
   */
  static generateToken(id, email) {
    const token = jwt.sign(
      {
        userId: id, email,
      },
      process.env.SECRET_KEY,
      {
        expiresIn: '7d',
      },
    );
    return token;
  }

  /**
   * @param  {} req
   * @param  {} res
   * @param  {} next
   */
  static verifyToken(req, res, next) {
    const token = req.headers.authorization;
    if (!token) {
      response.setError(401, 'No Authentication Token Provided');
      return response.send(res);
    }
    jwt.verify(token, process.env.SECRET_KEY, (err, decoded) => {
      if (err) {
        response.setError(403, 'Token not verified');
        return response.send(res);
      }
      const { userId, email } = decoded;
      req.user = { userId, email };
      return next();
    });
    return null;
  }
}

export default Authenticate;

const { body, validationResult } = require('express-validator');

const signUpValidator = [
  body('firstName')
    .isLength({ min: 3 })
    .withMessage(
      'firstName should not be empty and should be more than three characters',
    )
    .isAlpha()
    .withMessage('firstName should be an alpabet')
    .trim(),
  body('lastName')
    .isAlpha()
    .withMessage('lastName should be an alphabet')
    .isLength({ min: 3 })
    .withMessage(
      'lastName should not be empty and should be more than three characters',
    ),
  body('userName')
    .optional({ checkFalsy: true })
    .isAlphanumeric()
    .withMessage('username should be an alphanumeric')
    .trim(),
  body('email')
    .isEmail()
    .withMessage('email should not be empty and should be a valid email')
    .normalizeEmail(),
  body('password')
    .trim()
    .isLength({ min: 6 })
    .withMessage(
      'password should not be empty and should be more than 8 characters',
    ),
  body('dateOfBirth')
    .optional({ checkFalsy: true })
    .trim()
    .isDate()
    .withMessage('Invalid date'),
  function signUpValidation(req, res, next) {
    const errorValidation = validationResult(req);
    if (!errorValidation.isEmpty()) {
      return res.status(422).json({
        status: 422,
        error: errorValidation.array(),
      });
    }
    return next();
  },
];
export default signUpValidator;

